function largest_product = LargestProductInSeries
% This function gives the largest product of 13 consecutive numbers in a
% given series
    format longG
    series = "";
    global current_best;
    current_best = 0;
    series_split = split(series, "0");
    length = size(series_split);
    for i = 1:length(1)
        if strlength(series_split(i,1)) >= 13
            series_size = strlength(series_split(i, 1));
            difference = series_size - 13;
            for j = 0:difference
                value = getValue(extractBetween(series_split(i, 1), 1 + j, 13 + j));
                if value > current_best
                    current_best = value;
                end
            end
        end
    end
    
    largest_product = current_best;
    
    function value = getValue(string)
        product = 1;
        string = convertStringsToChars(string);
        for k = 1:strlength(string)
            product = product * str2double(string(k));
        end
        value = product;
    end
end
